package IndianaJones;

public class IndianaJones {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int numero = 1;
		for (int i = 0; i<=100 ; i++) {
		if (i == numero*7 ) {
			System.out.println(i);
		}
		System.out.println("Indiana Jones ha entrado en la valdosa" + i);
			
		}
		
	}

}
